import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import Airtable from 'airtable';
import ReastaurantElement from "../components/ReastaurantElement";

export default class RestaurantListPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      restaurants: [],
    };
  }

  componentDidMount() {
    var base = new Airtable({apiKey: 'key5hCfcpO5JBNaYj'}).base('appukpTZGXJdELj61');
    const restaurants = [];

    base('Restaurants').select({
        // Selecting the first 3 records in Grid view:
        maxRecords: 3,
        view: "Grid view"
    }).eachPage(function page(records, fetchNextPage) {
        // This function (`page`) will get called for each page of records.

        const restaurant = {};
        records.forEach(function(record) {
          restaurant.name = record.get('Nom');
          restaurant.description = record.get('Adresse');
          const images = record.get('Images');
          if(images && images.length > 0) {
            restaurant.image = images[0].url;
          }

          restaurants.push(restaurant);
        });

        this.setState({ restaurants });

        // To fetch the next page of records, call `fetchNextPage`.
        // If there are more records, `page` will get called again.
        // If there are no more records, `done` will get called.
        fetchNextPage();

    }, function done(err) {
        if (err) { console.error(err); return; }
    });


